import Axios from 'axios';
import React,{useState,useEffect} from 'react';
import Head from 'next/head';
 function Index(props){
    var currencylist=props.posts.rates;
     const currencies=[];
     const currencyValue=[];
    for(var i in currencylist)
    {
        currencies.push({key:i,value:currencylist[i]})
    } 
    console.log(currencies)
    const [amount,setamount]=useState(1);
    const [from,setFrom]=useState("USD");
    const [to,setTo]=useState("INR");
    const [result,setResult]=useState(false);
    const convertCurrency=()=>{/*https://api.exchangeratesapi.io/latest?base=${from}&symbols=${to}&amount=${amount}*/
        Axios.get(`https://api.exchangeratesapi.io/latest?base=USD`)
        .then((data)=>{
            console.log(data)
            const value=amount*data.data.rates[to]
            setResult(value)
        }).catch(error=>{
            console.log(error);
        })

    }
    return(
        
        <div className="Converter">
            <Head>
                <title>Currency converter</title>
            </Head>
            <h2>Latest Foreign currency exchange rates</h2>
            <table>
                {
                    currencies.map((post)=>(
                            <tr>
                                <th>{post.key}</th>
                                <th>{post.value}</th>
                            </tr>
                    ))
                }
            </table>
         
       
        <style jsx>
        {`
        @media only screen and (min-width:900px){
            .Converter{
                margin-top:3%;
                margin-left:40%;
                
            }
        }
        table {
            border-collapse: collapse;
            border-spacing: 0;
         
            overflow:scroll;
            height:400px;
            width: 40%;
            border: 1px solid #ddd;
          }
          
          th, td {
            text-align: left;
            padding: 16px;
          }
          
          tr:nth-child(even) {
            background-color: #f2f2f2;
          }
       
        .Form input, select {
            padding: 5px;
            margin: 5px 10px;
            border-radius: 5px;
            border: 1px solid rgba(119, 119, 119, 0.5);
            
        }
        
        .Form button {
            padding: 5px 10px;
            border-radius: 5px;
            background: rgb(11, 170, 59);
            color: white;
            border: none;
        }
       
        .Converter h3 {
            background-color: #ccc;
            border-radius: 10px;
            padding: 15px;
            width:300px;
            font-size: 30px;
            color:black;
            
        }
        
        .Converter h2 span {
            color: rgb(11, 170, 59);
        }
        `}
    </style>
    </div>

    );
}
export async function getStaticProps() {
 
    const res = await fetch('https://api.exchangeratesapi.io/latest?base=USD')
    const posts = await res.json()
    
    return {
      props: {
        posts,
      },
    }
  }
  export default Index;
